import React from 'react';
import httpHelper from '../helpers/httpHelper';

class createFarmFormComponent extends React.Component{

  constructor(props){
    super(props);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.state ={location:''}

  }

  componentDidMount() {
    this.getLocations()
  }

  handleChange(event){
    const target = event.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.name;
    this.setState({
      [name]: value
    });
  }

  getLocations(){
    let htmlLocations = [];
    httpHelper.serverReq('http://localhost:3009/api/v1/locations').then( (locations)=> {
      locations.success.data.forEach((location)=>{
        htmlLocations.push( <option value={location.id} >location.name</option>);
      });
      console.log(htmlLocations,'html locationa')
      this.setState({location: htmlLocations});
    })
  }

  handleSubmit(event){

  }

  render() {
    return(
      <form role="form" className="form-horizontal  form-groups-bordered">

        <div className="form-group">
          <label htmlFor="field-1" className="col-sm-3 control-label">Farm Name</label>

          <div className="col-sm-5">
            <input type="text" name="title" className="form-control" id="field-1" placeholder="Farm Title" />
          </div>
        </div>

        <div className="form-group">
          <label htmlFor="field-2" className="col-sm-3 control-label">Location</label>

          <div className="col-sm-5">
            <select name="location" className="form-control" id="field-2" placeholder="Placeholder" >
              <option>select a location</option>
              {this.state.location.forEach(function (element) {
                element
              })
              }
            </select>
          </div>
        </div>

        <div className="form-group">
          <label className="col-sm-3 control-label">Price Per Unit</label>

          <div className="col-sm-5">
            <div className="input-group">
              <input type="text" name="price_per_units" className="form-control" />
              <span className="input-group-addon">.00</span>
            </div>
          </div>
        </div>

        <div className="form-group">
          <label className="col-sm-3 control-label">Returns Percentage</label>

          <div className="col-sm-5">
            <div className="input-group">
              <input type="text" name="roi" className="form-control" />
              <span className="input-group-addon">%</span>
            </div>
          </div>
        </div>

        <div className="form-group">
          <label className="col-sm-3 control-label">Units available</label>

          <div className="col-sm-5">
            <div className="input-group">
              <input type="text" name="total_units" className="form-control" />
              <span className="input-group-addon">.00</span>
            </div>
          </div>
        </div>

        <div className="form-group">
          <label className="col-sm-3 control-label">Funding Starts</label>

          <div className="col-sm-5">
            <div className="input-group">
              <input type="date" name="funding_starts" className="form-control" />
            </div>
          </div>
        </div>

        <div className="form-group">
          <label className="col-sm-3 control-label">Funding Ends</label>

          <div className="col-sm-5">
            <div className="input-group">
              <input type="date" name="funding_ends" className="form-control" />
            </div>
          </div>
        </div>

        <div className="form-group">
          <label className="col-sm-3 control-label">Farm Opens</label>

          <div className="col-sm-5">
            <div className="input-group">
              <input type="date" name="farm_starts" className="form-control" />
            </div>
          </div>
        </div>

        <div className="form-group">
          <label className="col-sm-3 control-label">Farm Closes</label>

          <div className="col-sm-5">
            <div className="input-group">
              <input type="date" name="funding_ends" className="form-control" />
            </div>
          </div>
        </div>

        <div className="form-group">
          <label htmlFor="field-2" className="col-sm-3 control-label">Category</label>

          <div className="col-sm-5">
            <select name="location" className="form-control" id="field-2" placeholder="Placeholder" >
              <option>select a category</option>
            </select>
          </div>
        </div>



        <div className="form-group">
          <label htmlFor="field-1" className="col-sm-3 control-label">Farm photo Thumbnail</label>

          <div className="col-sm-5">
            <input type="file" className="form-control" id="field-file" placeholder="Placeholder" />
          </div>
        </div>
      </form>
    )
  }
}

export default createFarmFormComponent;
