import React from 'react';
import httpHelper from '../helpers/httpHelper';
import HelpBlock from '../components/HelpBlock';
import isAdmin from "../middlewares/isAdmin";
import Link from 'next/link';


class allFarmTableComponent extends React.Component{

  constructor(props){
    super(props);

    this.state ={allFarms:[]
    };


  }

  componentDidMount() {
    isAdmin();
    this.initDataTable();
    this.getFarmsData();

  }
  initDataTable(){
    jQuery(document).ready(function($) {
      var $table3 = jQuery("#table-3");

      var table3 = $table3.DataTable({
        "aLengthMenu": [
          [10, 25, 50, -1],
          [10, 25, 50, "All"]
        ]
      });

      // Initalize Select Dropdown after DataTables is created
      $table3.closest('.dataTables_wrapper').find('select').select2({
        minimumResultsForSearch: -1
      });

      // Setup - add a text input to each footer cell
      $('#table-3 tfoot th').each(function() {
        var title = $('#table-3 thead th').eq($(this).index()).text();
        $(this).html('<input type="text" class="form-control" placeholder="Search ' + title + '" />');
      });

      // Apply the search
      table3.columns().every(function() {
        var that = this;

        $('input', this.footer()).on('keyup change', function() {
          if (that.search() !== this.value) {
            that
              .search(this.value)
              .draw();
          }
        });
      });
    });
  }
  getFarmsData(){
    httpHelper.serverReq('http://localhost:3009/api/v1/farms/all').then( (data)=> {
      console.log(data);
      if (data.success){
        let farms = data.success.data;
        let editedFarms =  farms.map(farm=>
          <tr className="odd gradeX">
            <td>{farm.title}</td>
            <td>5 Months</td>
            <td>{farm.price_per_unit}</td>
            <td className="center">{farm.roi}%</td>
            <td className="center">12000</td>
            <td className="center">12000</td>
            <td>
              <Link href={farm.id}>
                <button type="button" className="btn btn-success">view</button>
              </Link>
            </td>
          </tr>
        );
        console.log(editedFarms,'edited farms')
        this.setState({allFarms : editedFarms});
      }
    })
  }

  render() {
    return(
      <table className="table table-bordered datatable" id="table-3">
        <thead>
        <tr className="replace-inputs">
          <th>Farm Name</th>
          <th>Duration</th>
          <th>Price</th>
          <th>Interest</th>
          <th>Total Available</th>
          <th>Units Bought</th>
          <th>Status</th>
        </tr>
        </thead>
        <tbody>

        {this.state.allFarms}
        </tbody>
        <tfoot>
        <tr>
          <th>Farm Name</th>
          <th>Duration</th>
          <th>Price</th>
          <th>Returns Percentage</th>
          <th>Units Available</th>
          <th>Units Bought</th>
          <th>Actions</th>
        </tr>
        </tfoot>
      </table>
    )
  }
}

export default allFarmTableComponent;
