import React from 'react';
import DashBoardLayOut from '../../layouts/AdminDashboardLayout';
import CreateFarmForm from '../../components/createFarm';
import isAdmin from  '../../middlewares/isAdmin';
import $ from 'jquery';
import httpHelper from "../../helpers/httpHelper";
import HelpBlock from "../../components/loginForm";
import FormHelpText from "../../components/signupForm";
import Router from "next/dist/client/router";

class createFarm extends React.Component{

  static async getInitialProps({ req }) {
    return {farmId:req.params.farmId};
  }

  constructor(props){
    super(props);
    this.state = {
      name:'',
      redirect:false,
      emailHelpText :{
        state:false,
        error:false,
        text:''
      },
      nameHelpText:{
        state:false,
        error:false,
        text:''
      },
      helpBlock:{
        state:false,
        error:false,
        text:''
      },
    }
  }

  componentDidMount() {
    isAdmin();

  }

  handleChange(e){

    const target = event.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.name;
    this.setState({
      [name]: value
    });
    switch (target.name) {
      case 'name':
        this.validateName(target.value);
        break;

      case 'email':
        this.validateEmail(target.value);
        break;
    }

  }

  handleSubmit(e){
    e.preventDefault();
    this.setState({ loginHelpBlock:{
        state:false,
        error:false,
        text:''
      }});

    let formData = new FormData();
    formData.append('name',this.state.name);
    formData.append('email',this.state.email);
    formData.append('permission',this.state.permission);



    HttpHelper.httpReq('http://localhost:3009/api/v1/user/admin/create',formData,'POST')
      .then((response)=>{
        if (response.success){
          this.setState({ helpBlock:{
              state:true,
              error:false,
              text:'user created successfully'
            }});
        }else{
          this.setState({ helpBlock:{
              state:true,
              error:true,
              text:'error creating user'
            }});
        }
      }).catch((error)=>{
      this.setState({ helpBlock:{
          state:true,
          error:true,
          text:'error creating user'
        }});
      });

  }

  validateEmail(email){
    if (email !== 'undefined'){
      return new Promise( (resolve,reject)=> {
        HttpHelper.httpReq('http://localhost:3009/api/v1/email/exists?email='+email,'','GET')
          .then((result)=>{
            console.log(result,'result');
            // console.log(JSON.stringify(result),'does email exist');
            if (result.message === true){
              this.setState({
                emailHelpText:{
                  state:true,
                  error:true,
                  text:'Email already exist'
                }
              });
            }else{
              this.setState({
                emailHelpText:{
                  state:false,
                  error:false,
                  text:' '
                }
              });
            }
            resolve(this.state.emailHelpText.error);
          }).catch((error)=>{console.log(error)});
      })
    }else{
      this.setState({
        emailHelpText:{
          state:true,
          error:true,
          text:'Email already exist'
        }
      })
    }


  }

  validateName(input){
    if (input.trim() === ''){
      this.setState({nameHelpText:{
          state:true,
          error:true,
          text:'Name is required'
        }});
      return false;
    }else{
      this.setState({nameHelpText:{
          state:false,
          error:false,
          text:'Name is required'
        }});
      return true;
    }
  }

  getAdminTypes(){
    httpHelper.serverReq('http://localhost:3009/api/v1/farms/all').then( (response)=> {
      if (response.success){
        this.setState({userTypes:response.success.data});
      }
    })
    }


  render() {
    return(
      <DashBoardLayOut>
        <hr/>
        <h2 id="form-head">Create Admin User</h2>
        <br/>
        <div className="row">
          <div className="col-md-12">
            <div className="panel panel-primary" data-collapsed="0" id="form">
              <div className="panel-body">

              </div>
            </div>
          </div>
        </div>
      </DashBoardLayOut>
    )
  }
}

export default createFarm;
