import React from 'react';
import DashBoardLayOut from '../../layouts/DashboardLayOut';
import httpHelper from '../../helpers/httpHelper';
import SingleFarm from '../../components/singleFarm';
import InfiniteScroll from 'react-infinite-scroll-component';
import $ from 'jquery';

class allFarms extends React.Component{

  constructor(props){
    super(props);
    this.state = {nextPage:false,isLastpage:false,hasMore:true,farmsUI:[]};
    this.loadMoreFarms = this.loadMoreFarms.bind(this);
  }

  static async getInitialProps({ req }) {
    return {};
  }
  componentDidMount() {
    this.loadMoreFarms();
  }

  loadMoreFarms(){
    httpHelper.serverReq(`http://localhost:3009/api/v1/farms/all?paginate=true${this.state.nextPage?"&page="+this.state.nextPage:""}`).then( (data)=> {
      if(data.success){
        if (data.success.data.nextPage){

          this.setState({nextPage:data.success.nextPage,hasMore:true});

        }else{
          this.setState({nextPage:false,hasMore:false});

        }

        let farmsUI = data.success.data.map(function (farm) {
          return  <SingleFarm key={farm.id} details={farm} />;
        });
        this.setState({farmsUI:farmsUI});
      }
    })
  }


  render() {

    return(
      <DashBoardLayOut>
        <div className="col-sm-12">
          <div className="panel panel-primary" data-collapsed="0">

            <div className="panel-body container">
              <div className="row">
                <img src="./assets/images/jarren-simmons-35780-unsplash.jpg" alt="..."
                     className="img-responsive details col-md-6"/>
                  <div className="col-md-4">
                    <div className="row text-center">
                      <h2 className="text-center">Description</h2>
                      <p className="describe">This goat farm is on a total of 5400 hectares of farmland located in oloba
                        village,Osun State. </p>
                      <div className="col-md-6">
                        <p>Price Per Unit</p>
                        <p className="ptag">₦60000</p>
                      </div>
                      <div className="col-md-6">
                        <p className="">Returns</p>
                        <p className="ptag">50%</p>
                      </div>
                    </div>
                    <div className="row text-center">
                      <div className="col-md-6">
                        <p>Duration</p>
                        <p className="ptag">9 Months</p>
                      </div>
                      <div className="col-md-6">
                        <p className="">Units Available</p>
                        <p className="ptag">5000</p>
                      </div>
                    </div>
                    <div className="row text-center">
                      <div className="col-md-6">
                        <a href="#" className="btn btn-primary" role="button">Fund</a>
                      </div>
                      <div className="col-md-6">
                        <a href="#" className="btn btn-primary" role="button">follow <i className="fa fa-heart"></i></a>
                      </div>
                    </div>
                    <div className="insured">
                      <p style={{color: '#77B112'}}><b>This farm is insured by</b><img src="assets/images/leadway.png"
                                                                                   alt=""/></p>
                    </div>

                  </div>
              </div>
            </div>
          </div>
        </div>
      </DashBoardLayOut>
    )
  }
}

export default allFarms;
