import React from 'react';
import httpHelper from '../helpers/httpHelper';
import ActionButton from '../components/userStatusButton';
import isAdmin from "../middlewares/isAdmin";
import FarmTR  from "../components/farmsTableRow";
import Link from 'next/link';


class allFarmTableComponent extends React.Component{

  constructor(props){
    super(props);
    this.handleStatusChange = this.handleStatusChange.bind(this);
    this.state ={allFarms:[]
    };


  }

  componentDidMount() {
    isAdmin();
    this.usersData();

  }

  handleStatusChange(id,status){
    let users = this.state.users;
    users.forEach( (user,index)=> {
      if (user.id === id){
        if (status){

          users[index].user_status_name = "active";
        } else{
          users[index].user_status_name = "de-active";

        }
      }
    })
  }

  usersData(){
    httpHelper.httpReq("http://localhost:3009/api/v1/users").then( (response) =>{
      if (response.success){
      let userDom =  response.success.data.users.map( (user)=> {
         return <tr key={user.id}>
            <td>{user.name}</td>
            <td>{user.email}</td>
            <td>{user.user_type_name}</td>
            <td>{user.status_name}</td>
            <td><ActionButton user={user} onStatusBtn={this.handleStatusChange} /></td>
          </tr>
        })
      }
    })
  }


  render() {
    return(
      <table className="table table-bordered datatable" id="table-3">
        <thead>
        <tr className="replace-inputs">
          <th> Name</th>
          <th>Email</th>
          <th>Access Level</th>
          <th>Status</th>
          <th>Action</th>
        </tr>
        </thead>
        <tbody>

        {this.state.allFarms}
        </tbody>
        <tfoot>
        <tr>
          <th> Name</th>
          <th>Email</th>
          <th>Access Level</th>
          <th>Status</th>
          <th>Action</th>
        </tr>
        </tfoot>
      </table>
    )
  }
}

export default allFarmTableComponent;
