class HttpHelper {
  static httpReq(url = '', data,method) {
      let contentType;
      if (data instanceof  FormData){
         contentType = 'undefined';
      }else{
         contentType = 'application/json';
      }

     let parameters = {
       method: (method !== "undefined" ?method:'GET'), // *GET, POST, PUT, DELETE, etc.
       mode: 'cors', // no-cors, cors, *same-origin

       cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
       credentials: 'same-origin', // include, *same-origin, omit
       headers: {
         'Content-Type': contentType,
         'Access-Control-Allow-Credentials':true
         // 'Content-Type': 'application/x-www-form-urlencoded',
       },
       redirect: 'follow', // manual, *follow, error
       referrer: 'no-referrer', // no-referrer, *client
       body: contentType === 'undefined'?data:JSON.stringify(data), // body data type must match "Content-Type" header
     }

      if (data === ''){
        delete parameters.body;
      }

      console.log(parameters,'parameters');

      // Default options are marked with *
      return fetch(url, parameters )
        .then( (response)=> {
          return response.json()
          // switch (response.status) {
          //   case "200":
          //     return response.json();
          //     break;
          //   default:
          //     return new Promise(function (resolve,reject) {
          //       reject({error:response.status,text:response.statusText});
          //     })
          // }
        }); // parses JSON response into native JavaScript objects
    }
}
export default HttpHelper;
