import http from '../helpers/httpHelper'
let isAdmin =  function(){

    http.serverReq('www-dev.easycow.com:3009/api/v1/user').then(function (data) {

      if (data.success.user_type === 2){
        return true
      }
      return false;
    })

}

export default isAdmin;
