import http from '../helpers/httpHelper'
let isAdmin =  function(res){


    http.httpReq('www-dev.easycow.com:3009/api/v1/user').then(function (data) {
      if (data.success.user_type === 2){

        return true
      }
      return   res.redirect('/login')

    }).catch(function () {
      return   res.redirect('/login');

    })

}

export default isAdmin;
